import React, { useEffect } from 'react';
import './App.css';
import stylesStars from "./styles/stars.module.scss";

function App() {
  const numStars = [120, 10, 5];
  const starContainers = ['rear', 'center', 'front'];

  function prepareStage() {
    // could have used haml / pug, but this keeps our markup tidy
    const star = document.createElement('div');
    star.classList.add('stars__star');

    numStars.forEach((set, index) => {
      const container = document.querySelector(`.stars__${starContainers[index]}`);

      for(let i = 0; i < numStars[index]; i ++) {
        container?.appendChild(star.cloneNode(true));
      }
    });
  }

    const stars = document.getElementsByClassName(stylesStars.stars)[0];
    const starsAnimated = 'stars--animated';
    console.log(stars);
    stars?.classList.add(starsAnimated);


  return (
    <div>
      <h1>Hello worl !</h1>
      <section className={stylesStars.section}>
        <div className={stylesStars.stars }>
          <div className={stylesStars.stars__rear}></div>
          <div className={stylesStars.stars__center}></div>
          <div className={stylesStars.stars__front}></div>
        </div>
        <div className={stylesStars.stratosphere}></div>
      </section>
    </div>
  );
}

export default App;
