  })
  timeline.to(".profil", 
  {
    y: +800,
    duration: 3,
    delay: 1,
  });
  timeline.to(".profil", 
  {
    zIndex: 2,
    x: 600,
    scale: 0.7,
    delay: 0
  });
  timeline.to(".profil", 
  {
    y: -600,
    duration: 1.7,
    delay: 1,
  });
  timeline.to(".profil", 
  {
    y: +600,
    duration: 3,
    delay: 1,
  });
  timeline.to('.profil',
  {
    delay: 0,
    scale: 1,
