import React, { useEffect, useState, useRef} from 'react';
import './App.css';
import "./styles/stars.scss";
import arrow_down from "./assets/arrow_down.png";
import { gsap } from "gsap";
import Typewriter from 'typewriter-effect';


function App() {
  const [maxAnimation, setMaxAnimation] = useState(true);
  const [startWritting, setSartWritting] = useState(false);
  const [h1Write, setH1Write] = useState<JSX.Element>();



  useEffect(() => {
    const numStars = [120, 10, 5];
    const starContainers = ['rear', 'center', 'front'];

    function prepareStage() {
      // could have used haml / pug, but this keeps our markup tidy
      const star = document.createElement('div');
      star.classList.add('stars__star');

      numStars.forEach((set, index) => {
        const container = document.querySelector(`.stars__${starContainers[index]}`);
        console.log(container);
        for(let i = 0; i < numStars[index]; i ++) {
          container?.appendChild(star.cloneNode(true));
        }
      });
    }

    prepareStage();

    const stars = document.querySelector('.stars');
    const starsAnimated = 'stars--animated';
    stars?.classList.add(starsAnimated);
}, []);

useEffect(() => {
    let title = document.getElementById("title");
    let firstMontains = document.getElementById("firstMontains");
    let secondMontains = document.getElementById("secondMontains");
    let moune = document.getElementById("moune");
    let ground = document.getElementById("ground");
    let max = document.getElementById("max");
    console.log(title);
    window.addEventListener("scroll", () => {
      let value = window.scrollY;
      if (!value)
        setMaxAnimation(true);
      else
        setMaxAnimation(false);
      if (title && firstMontains && secondMontains
        && moune && ground && max)
      {
        moune.style.marginTop = 1.5 * value + 'px';
        title.style.marginTop = 1.5 * value + 'px';
        firstMontains.style.top = value * 0.5 + 'px';
        secondMontains.style.top = value * 1.2 + 'px';
        ground.style.top = value * 0 + 'px';
        console.log(value);
        if (value > 560)
        {
           moune.style.backgroundColor = '#000';
           moune.style.boxShadow = "none";
           moune.style.transform = `scale(${(value / 560) * ((value - 500) / 100)})`;
        }
        else
        {
          moune.style.backgroundColor = 'rgba(255, 255, 255, 0.9)';
          moune.style.boxShadow = " 0px 0px 100px 10px rgba(240, 177, 177, 0.763)";
          moune.style.transform = "scale(1)";
        }

        console.log(value);
        if (value > 1000)
          setSartWritting(true);

      }
    });
  }, []);

  useEffect(() => {

  const timeline = gsap.timeline();
  timeline.to(".profil", 
  {
    zIndex: 0,
    x: 0,
    scale: 0.3,
    duration: 0.2
  });
  timeline.to(".profil", 
  {
    y: -900,
    duration: 1.7,
    delay: 1,
  })
  timeline.to(".profil", 
  {
    y: +900,
    duration: 3,
    delay: 1,
  });
  timeline.to(".profil", 
  {
    zIndex: 1,
    x: 500,
    scale: 0.4,
    delay: 0
  });
  timeline.to(".profil", 
  {
    y: -800,
    duration: 1.7,
    delay: 1,
  })
  timeline.to(".profil", 
  {
    y: +800,
    duration: 3,
    delay: 1,
  });
  timeline.to(".profil", 
  {
    zIndex: 2,
    x: 600,
    scale: 0.7,
    delay: 0
  });
  timeline.to(".profil", 
  {
    y: -600,
    duration: 1.7,
    delay: 1,
  });
  timeline.to(".profil", 
  {
    y: +600,
    duration: 3,
    delay: 1,
  });
  timeline.to('.profil',
  {
    delay: 0,
    scale: 1,
    x: 0,
    zIndex: 5,
  });
  timeline.to('.profil',
  {
    y: -600,
    duration: 3,
    delay: 1,
  })

  const titleLine = gsap.timeline();
  titleLine.to(".Title", 
  {
    y: -180 + 'px',
    duration: 2,
    delay: 0.3,
  })

  }, []);

  useEffect(() => {
    if (startWritting)
    {
        const typeWritter = <Typewriter
        options={{deleteSpeed: 15, delay: 20}}
        onInit={(typewriter) => {
        typewriter
        .typeString("<h1>Hello World !<br/><br/>")
        .pauseFor(300)
        .typeString("I\'m Maxence. I'm a dev ")
        .typeString('<strong><span style="color:#C97B84">Typescript</span> / <span style="color:#A85751">React</span> / <span style="color:#7D2E68">css</span> / <span style="color:#A85751">html</span></strong>')
        .pauseFor(1000)
        .deleteChars(31)
        .typeString('<strong><span style="color:#C97B84">NodeJs</span> / <span style="color:#A85751">C</span> / <span style="color:#7D2E68">C++</span></strong>')
        .pauseFor(1000)
        .deleteChars(16)
        .typeString('<strong><span style="color:#C97B84">Fullstack and Passionate !</span></strong></')
        .pauseFor(1000)
        .typeString('<br/><br/><p>Curieux et passionné par le code, je développe depuis plus d’un an et demi de nombreuses compétences techniques.<br/><br/>\
        Aujourd\’hui je cherche à mettre mes compétences au service de projets qui font du bien à la société.<br/><br/>\
        Je souhaite rejoindre une équipe de développeurs passionnés et qui ont des bonnes pratiques de développement auprès desquelles je pourrais continuer de grandir.<br/><br/>\
        Fast learner, je vous assure que mon manque d\'expérience sera rapidement comblée par ma soif d’apprendre.<br/><br/>\
        Je continue de progresser chaque jour, guidé par la voie du software craftsmanship.<br/><br/>\
        M’accorder votre confiance, c’est vous assurer de recruter un élément passionné, curieux, et déterminé.</p>')
        .start();
        }}/>
        setH1Write(typeWritter);
    }

  }, [startWritting])

  useEffect(() => {
    const max = document.getElementById('max');
    console.log(maxAnimation);
    if (!maxAnimation && max)
    {
      max.style.opacity = '0';
    }
    else if (max)
    {
      max.style.opacity = '1';
    }
  }, [maxAnimation])



  return (
    <div>
      <div className={`containerScrollHorizon`}>
          <span className={`textScroll`}>
          top          .          profil          .          harsSkills          .          projects          .          contact          .          top          .          profil          .          harsSkills          .          projects          .          contact          .          &nbsp;
          </span>
          <span className={`textScroll`}>
          top          .          profil          .          harsSkills          .          projects          .          contact          .          top          .          profil          .          harsSkills          .          projects          .          contact          .          &nbsp;
          </span>
      </div>
      <div className='moune' id='moune'></div>
      <section className="section one">
        <div className="stars">
          <div className="stars__rear"></div>
          <div className="stars__center"></div>
          <div className="stars__front"></div>
        </div>
        <div className="stratosphere"></div>
        <div className='montainsContainer'>
          <div className="secondMontains" id='secondMontains'></div>
          <h1 className='Title' id='title'>Software Engineer</h1>
          <div className="firstMontains" id='firstMontains'></div>
          <div className="ground" id='ground'></div>
          <div className="profil" id='max'></div>
          <div className='scrollDown'>
            <p>scoll down</p>
            <img alt="arrow" src={arrow_down} width={32} height={32}></img>
          </div>
        </div>
      </section>
      <section className='section two'>
        <div className='presentationContainer'>
          <div id='textWrite'>{h1Write}</div>
        </div>

      </section>
    </div>
  );
}

export default App;
